<?php  // 载入Workerman  use Workerman\Worker;  require_once __DIR__ . '/workerman-for-win-master/Autoloader.php';  // 创建一个Worker监听2000端口，使用WebSocket协议通讯  $ws_worker = new Worker('websocket://127.0.0.1:2000');  // 启动4个进程对外提供服务  $ws_worker->count = 4;  // 当收到客户端发来的数据$data后返回给客户端  $ws_worker->onMessage = function($connection, $data) {    // 向客户端发送Hello $data    $connection->send('Hello ' . $data);  };  // 运行Worker  Worker::runAll();