<?php  use Workerman\Worker;  use Workerman\Lib\Timer;  require_once __DIR__ . '/workerman-for-win-master/Autoloader.php';  $worker = new Worker('websocket://127.0.0.1:2000');  // 进程启动时设置一个定时器，定时向所有客户端连接发送数据  $worker->onWorkerStart = function($worker) {    // 每2秒一次，遍历当前进程所有的客户端连接，发送当前服务器的时间    Timer::add(2, function() use($worker) {      foreach($worker->connections as $connection) {        $connection->send(time());      }    });  };  Worker::runAll();